import { Component, OnInit, ViewChild } from '@angular/core';
import * as Highcharts from 'highcharts';
import { ChartComponent } from 'highcharts-angular';
import { Options } from 'highcharts';

@Component({
  selector: 'app-line-chart',
  templateUrl: './line-chart.component.html',
  styleUrls: ['./line-chart.component.css'],
})
export class LineChartComponent implements OnInit {
  Highcharts: typeof Highcharts = Highcharts;
  chartOptions: Options = {
    title: {
      text: 'Live Data Line Chart',
    },
    xAxis: {
      type: 'datetime',
    },
    series: [
      {
        type: 'line',
        name: 'Live Data',
        data: [], // Start with an empty data array
      },
    ],
  };

  @ViewChild('chart') chart: ChartComponent; // ViewChild to access the chart component

  constructor() {}

  ngOnInit(): void {
    setInterval(() => {
      const timestamp = new Date().getTime();
      const value = Math.random() * 100;

      if (this.chart && this.chart.chart) {
        this.chart.chart.series[0].addPoint([timestamp, value], true, this.chart.chart.series[0].data.length >= 10);
      }
    }, 1000);
  }
}
