import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';
import { Options } from 'highcharts';
import { HttpClient } from '@angular/common/http';


// Define an interface for a single record
interface SensorRecord {
  temperature: number;
  humidity: number;
  minute: string;
}

@Component({
  selector: 'app-line-chart',
  templateUrl: './line-chart.component.html',
  styleUrls: ['./line-chart.component.css'],
})
export class LineChartComponent implements OnInit {
  Highcharts: typeof Highcharts = Highcharts;
  chartOptions: Highcharts.Options = {
    title: {
      text: 'Live Data Line Chart',
    },
    xAxis: {
      type: 'datetime',
    },
    series: [
      {
        type: 'line',
        name: 'Live Data',
        data: [], // Start with an empty data array
      },
    ],
  };
  chart: Highcharts.Chart | undefined; // Declare chart reference

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    // Create the chart instance
    this.chart = Highcharts.chart('chart-container', this.chartOptions);

    this.makeAPICall();

    setInterval(() => {
      // const timestamp = new Date().getTime();
      // const value = Math.random() * 100;

      // if (this.chart) {
      //   this.chart.series[0].addPoint(
      //     [timestamp, value],
      //     true,
      //     this.chart.series[0].data.length >= 10
      //   );
      // }


     this.makeAPICall();



    }, 60000); // 60000 milliseconds = 1 minute
  }

  makeAPICall(): void {
    const apiUrl = "http://localhost:8000/liveRecordsSensorData";
    const headers = {
      "Content-Type": "application/json"
    };

    this.http.get(apiUrl, { headers }).subscribe(
      (response: any) => {
        console.log("API response", response);

        console.log(response.liveRecords[0]);

        const liveRecords: SensorRecord[] = response.liveRecords;

        const chart = this.chart; // Store this.chart in a local variable

        if (chart) {
          // Clear existing series data
          // if (chart.series[0]) {
          //   chart.series[0].setData([]);
          // }

          // if (chart.series[1]) {
          //   chart.series[1].setData([]);
          // }

          // If you have a separate series for humidity, clear its data as well
          // if (chart.series[1]) {
          //   chart.series[1].setData([]);
          // }

          liveRecords.forEach(record => {
            const timestamp = new Date(record.minute).getTime();

            const temperature = record.temperature;
            const humidity = record.humidity;

            console.log(temperature);
            console.log(timestamp);


            // Add temperature data to the chart's first series
            if (chart.series[0]) {
              chart.series[0].addPoint([timestamp, temperature]);
            }
            else{
              console.log("no series");
            }


            if (chart.series[1]) {
              chart.series[1].addPoint([humidity, temperature]);
            }
            else{
              console.log("no series");
            }

            // Add humidity data to the chart's second series (if you have it)
            // if (chart.series[1]) {
            //   chart.series[1].addPoint([timestamp, humidity]);
            // }
          });
        }
        else{
          console.log("no chart?");
        }
      },
      (error) => {
        console.log("API error", error);
      }
    );
  }


}
